rule.definition:
a!localVariables(
  local!selectedCustomValue,
  local!justSelectedCustomValue,
  local!userInPicker,
  local!justSelectedUser,
  {
    #"SYSTEM_SYSRULES_pickerFieldCustom"(
      label: "Select a Value",
      suggestFunction: #"_a-0000e325-2aec-8000-9ba2-011c48011c48_55313"(
        _
      ),
      value: local!selectedCustomValue,
      saveInto: {
        local!selectedCustomValue,
        local!justSelectedCustomValue,
        a!save(
          local!selectedCustomValue,
          null
        )
      }
    ),
    #"SYSTEM_SYSRULES_textField"(
      label: "Last Selected ID",
      readOnly: true,
      value: "ID of last selected value was " & local!justSelectedCustomValue
    ),
    #"SYSTEM_SYSRULES_pickerFieldUsers"(
      label: "Choose a User",
      value: local!userInPicker,
      saveInto: {
        local!userInPicker,
        local!justSelectedUser,
        a!save(
          local!userInPicker,
          null
        )
      },
      maxSelections: 1
    ),
    #"SYSTEM_SYSRULES_textField"(
      label: "Last Selected User",
      readOnly: true,
      value: if(
        not(isnull(local!justSelectedUser)),
        user(
          loggedInUser(),
          "firstName"
        ) & "'s choice was " & user(
          index(local!justSelectedUser,1),
          "firstName"
        ),
        user(
          loggedInUser(),
          "firstName"
        ) & "$ choice is awaited"
      )
    )
  }
)
