rule.definition:
a!localVariables(
  /* These are the available dropdown options */
  local!possibleDropdownOptions: {
    "Europe",
    "Australia",
    "South America"
  },
  /* Check if the provided value is in the list */
  local!valueIsInvalid: not(
    contains(
      local!possibleDropdownOptions,
      ri!value
    )
  ),
  /* If the value is not in the list, append the invalid value */
  local!dropdownOptionsToDisplay: if(
    local!valueIsInvalid,
    append(
      local!possibleDropdownOptions,
      ri!value
    ),
    local!possibleDropdownOptions
  ),
  #"SYSTEM_SYSRULES_formLayout_v1"(
    contents: {
      #"SYSTEM_SYSRULES_textField"(
        value: local!valueIsInvalid
      ),
      #"SYSTEM_SYSRULES_dropdownField"(
        label: "Select Continent",
        value: ri!value,
        choiceLabels: local!dropdownOptionsToDisplay,
        choiceValues: local!dropdownOptionsToDisplay,
        saveInto: {
          /* Save the selected value */
          ri!value,
          /* If the original value was invalid, remove it from the available options */
          if(
            local!valueIsInvalid,
            a!save(
              local!dropdownOptionsToDisplay,
              local!possibleDropdownOptions
            ),
            {}
          )
        }
      )
    }
  )
)
